import React, { useState } from 'react';
import ProductList from '../components/ProductList';
import Cart from '../components/Cart';

const Products = ({ onAddToCart, cartItems, showCart, setShowCart }) => {
  const [selectedCategory, setSelectedCategory] = useState('todos');
  
  // Catálogo completo de flores exóticas
  const allProducts = [
    {
      id: 1,
      name: 'Orquídea Negra da Selva',
      price: 249.99,
      image: 'https://images.unsplash.com/photo-1566566220366-95f6a5a516f9?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      description: 'Raridade amazônica, floresce apenas uma vez por ano com pétalas aveludadas.',
      category: 'orquideas',
      origin: 'Amazônia Brasileira',
      careLevel: 'Avançado'
    },
    {
      id: 2,
      name: 'Rosa do Deserto Negra',
      price: 189.99,
      image: 'https://images.unsplash.com/photo-1517457373958-b7bdd4587205?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      description: 'Variedade rara do Oriente Médio, adaptada a condições extremas.',
      category: 'suculentas',
      origin: 'Oriente Médio',
      careLevel: 'Intermediário'
    },
    {
      id: 3,
      name: 'Flor de Jade Selvagem',
      price: 299.99,
      image: 'https://images.unsplash.com/photo-1598880940080-ff9a29891b85?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      description: 'Trepadeira nativa das Filipinas com flores em formato de garra.',
      category: 'trepadeiras',
      origin: 'Filipinas',
      careLevel: 'Avançado'
    },
    {
      id: 4,
      name: 'Lótus Negra do Himalaia',
      price: 229.99,
      image: 'https://images.unsplash.com/photo-1597522787150-3f4a9f45ea88?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      description: 'Cresce em altitudes extremas, simbolizando pureza na adversidade.',
      category: 'aquaticas',
      origin: 'Himalaia',
      careLevel: 'Intermediário'
    },
    {
      id: 5,
      name: 'Flor Cadáver Silvestre',
      price: 399.99,
      image: 'https://images.unsplash.com/photo-1582794543139-8ac9cb0f7b11?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      description: 'A maior flor do mundo, raramente cultivada fora de seu habitat natural.',
      category: 'raras',
      origin: 'Sumatra',
      careLevel: 'Especialista'
    },
    {
      id: 6,
      name: 'Hibisco Azul de Madagascar',
      price: 179.99,
      image: 'https://images.unsplash.com/photo-1593483316242-efb5420596ca?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      description: 'Variedade endêmica da ilha africana, com tonalidades vibrantes.',
      category: 'hibiscos',
      origin: 'Madagascar',
      careLevel: 'Iniciante'
    },
    {
      id: 7,
      name: 'Flor do Paraíso Negra',
      price: 259.99,
      image: 'https://images.unsplash.com/photo-1580229081091-9d8d8a1a8a0d?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      description: 'Variedade rara da África do Sul com forma exótica e coloração intensa.',
      category: 'exoticas',
      origin: 'África do Sul',
      careLevel: 'Intermediário'
    },
    {
      id: 8,
      name: 'Jasmim Estrela da Noite',
      price: 199.99,
      image: 'https://images.unsplash.com/photo-1516048015710-7a3b4c86be43?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80',
      description: 'Libera um perfume intenso apenas durante a noite, raro em cultivo.',
      category: 'aromáticas',
      origin: 'Sudeste Asiático',
      careLevel: 'Avançado'
    }
  ];

  // Filtra produtos por categoria
  const filteredProducts = selectedCategory === 'todos' 
    ? allProducts 
    : allProducts.filter(product => product.category === selectedCategory);

  // Categorias disponíveis
  const categories = [
    { id: 'todos', name: 'Todas as Flores' },
    { id: 'orquideas', name: 'Orquídeas Raras' },
    { id: 'suculentas', name: 'Suculentas Exóticas' },
    { id: 'trepadeiras', name: 'Trepadeiras' },
    { id: 'hibiscos', name: 'Hibiscos' },
    { id: 'raras', name: 'Espécies Raras' }
  ];

  const removeFromCart = (productId) => {
    onAddToCart(cartItems.filter(item => item.id !== productId));
  };

  return (
    <div className="products-page">
      <div className="products-header">
        <h2>Nossas Flores Selvagens</h2>
        <p className="page-subtitle">
          Cada flor em nossa coleção foi cuidadosamente selecionada por sua raridade e beleza única.
        </p>
        
        {/* Filtro por categoria */}
        <div className="category-filter">
          <label htmlFor="category-select">Filtrar por categoria:</label>
          <select
            id="category-select"
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
          >
            {categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>
      </div>
      
      {/* Carrinho ou lista de produtos */}
      {showCart ? (
        <Cart 
          items={cartItems} 
          onRemove={removeFromCart} 
          onClose={() => setShowCart(false)} 
        />
      ) : (
        <>
          <div className="products-grid">
            <ProductList 
              products={filteredProducts} 
              onAddToCart={onAddToCart} 
            />
          </div>
          
          {filteredProducts.length === 0 && (
            <div className="no-products">
              <p>Nenhuma flor encontrada nesta categoria.</p>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default Products;
